import { put, takeLatest, all, call } from "redux-saga/effects";
import { LOGIN, REGISTER, GET_USER_INFO, LOGOUT } from "../constants/auth";
import { loginSuccess, loginError, registerSuccess, registerError, getUserInfoSuccess } from "../actions/auth";
import Request from "../utils/request";
import Authendication from "../utils/authendication";
export function* handleLogin(action) {
  const url = "/auth/login";

  try {
    const response = yield call(Request, url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        phone: action.phone,
        password: action.password
      })
    });
    Authendication.saveAuthToCookie(response.data.token);
    yield put(loginSuccess(response.data.user));
  } catch (error) {
    yield put(loginError(error.message));
  }
}
export function* handleRegister(action) {
  const url = "/auth/register";

  try {
    const response = yield call(Request, url, {
      method: "POST",
      body: JSON.stringify({
        phone: action.phone,
        password: action.password
      })
    });
    Authendication.saveAuthToCookie(response.data.token);
    yield put(registerSuccess(response.data.user));
  } catch (error) {
    yield put(registerError(error.message));
  }
}
export function* handleGetUserInfo(action) {
  const url = "/auth/me";

  try {
    const response = yield call(Request, url, {}, action.accessToken);
    yield put(getUserInfoSuccess(response.data));
  } catch (error) {// yield put(registerError(error.message));
  }
}
export function* handleLogout(action) {
  Authendication.logout();
}
export default function* rootSaga() {
  yield all([yield takeLatest(LOGIN, handleLogin), yield takeLatest(REGISTER, handleRegister), yield takeLatest(GET_USER_INFO, handleGetUserInfo), yield takeLatest(LOGOUT, handleLogout)]);
}
