import { put, takeLatest, all, call } from "redux-saga/effects";
import { CREATE_ORDER } from "../constants/order";
import { createOrderSuccess, createOrderError } from "../actions/order";
import Request from "../utils/request";
export function* handleCreateOrder(action) {
  const url = "/order";

  try {
    const response = yield call(Request, url, {
      method: "POST",
      body: JSON.stringify(action.orderInfo)
    });
    yield put(createOrderSuccess(response.data));
  } catch (error) {
    yield put(createOrderError(error.message));
  }
}
export default function* rootSaga() {
  yield all([yield takeLatest(CREATE_ORDER, handleCreateOrder)]);
}
